package first;
import java.util.Map;
import java.util.HashMap;
import java.util.Iterator;

class CheckInclusion {
    public static void main() {
        String s = "";
        String t = "";
        // String s = "ab";
        // String t = "eidbaooo";
        boolean v = checkInclusion(s, t);
        System.out.println(v);
    }
    public static boolean checkInclusion(String s, String t) {
        if (s.length() > t.length()) {
            return false;
        }
        Map<String, Integer> sEncode = encodeStr(s);
        int sz = t.length();
        int start = 0, end = s.length();
        Map<String, Integer> tEncode = encodeStr(t.substring(start, end));
        while (end <= sz) {
            if (equals(sEncode, tEncode)) {
                return true;
            }
            char c = t.charAt(start);
            Integer v = tEncode.get(c + "");
            tEncode.put(c + "", v - 1);
            start++;
            if (end + 1 > sz) break;
            c = t.charAt(end);
            v = tEncode.get(c + "");
            if (v == null) {
                tEncode.put(c + "", 1);
            } else {
                tEncode.put(c + "", v + 1);
            }
            end++;
        }
        return false;
    }

    private static Map<String, Integer> encodeStr(String t) {
        Map<String, Integer> value = new HashMap<String, Integer>();
        char[] characters = t.toCharArray();
        for (char c : characters) {
            Integer num = value.get(c + "");
            if (num == null) {
                value.put(c + "", 1);
            } else {
                value.put(c + "", num + 1);
            }
        }

        return value;
    }

    private static boolean equals(Map<String, Integer> a, Map<String, Integer> b) {
        Iterator<String> iter  = a.keySet().iterator();
        while (iter.hasNext()) {
            String key = iter.next();
            Integer value = a.get(key);

            Integer bValue = b.get(key);
            if (bValue == null || !bValue.equals(value)) {
                return false;
            }
        }

        return true;
    }
}